SELECT AVG(a.score) as score, a.subject_id
FROM score a INNER JOIN teacher_subject b  ON a.subject_id = b.subject_id
WHERE b.teacher_id = #{teacherScore.teacherId} AND b.school_year = #{teacherScore.schoolYear}
GROUP BY subject_id
LIMIT #{teacherScore.start}, #{teacherScore.size}

