mapper.select:
SELECT id FROM teacher_subject
WHERE teacher_id = #{teacherSubject.teacherId}
AND subject_id = #{teacherSubject.subjectId}
AND school_year = #{teacherSubject.schoolYear}

mapper.update:
UPDATE teacher_subject
set teacher_id = #{teacherSubject.teacherId},
    subject_id = #{teacherSubject.subjectId},
    school_year = #{teacherSubject.schoolYear}
WHERE id = #{teacherSubject.id}
