mapper.select:
SELECT id FROM teacher_subject
WHERE teacher_id = #{teacherSubject.teacherId}
AND subject_id = #{teacherSubject.subjectId}
AND school_year = #{teacherSubject.schoolYear}

mapper.update:
UPDATE teacher_subject
set teacher_id = #{teacherSubject.teacherId},
    subject_id = #{teacherSubject.subjectId},
    school_year = #{teacherSubject.schoolYear}
WHERE id = #{teacherSubject.id}

mapper.select:
SELECT AVG(b.score) as score, b.subject_id
FROM teacher_subject a INNER JOIN score b  ON a.subject_id = a.subject_id
WHERE a.teacher_id = #{teacherScore.teacherId} AND a.school_year = #{teacherScore.schoolYear}
GROUP BY subject_id
LIMIT #{teacherScore.start}, #{teacherScore.size}

SELECT MAX(b.score) as score, b.subject_id
 FROM teacher_subject a INNER JOIN score b  ON a.subject_id = a.subject_id
 WHERE a.teacher_id = #{teacherScore.teacherId} AND a.school_year = #{teacherScore.schoolYear}
 GROUP BY subject_id
 LIMIT #{teacherScore.start}, #{teacherScore.size}

SELECT MIN(b.score) as score, b.subject_id
FROM teacher_subject a INNER JOIN score b  ON a.subject_id = b.subject_id
WHERE a.teacher_id = #{teacherScore.teacherId} AND a.school_year = #{teacherScore.schoolYear}
GROUP BY subject_id
LIMIT #{teacherScore.start}, #{teacherScore.size}

SELECT AVG(b.score) as score, b.subject_id, a.teacher_id
FROM  teacher_subject a INNER JOIN  score b  ON a.subject_id = b.subject_id
WHERE a.school_year = #{schoolScore.schoolYear}
GROUP BY subject_id, teacher_id
LIMIT #{schoolScore.start}, #{schoolScore.size}

SELECT MAX(b.score) as score, b.subject_id, a.teacher_id
FROM  teacher_subject a INNER JOIN  score b  ON a.subject_id = b.subject_id
WHERE a.school_year = #{schoolScore.schoolYear}
GROUP BY subject_id, teacher_id
LIMIT #{schoolScore.start}, #{schoolScore.size}

SELECT MAX(b.score) as score, b.subject_id, a.teacher_id
FROM  teacher_subject a INNER JOIN  score b  ON a.subject_id = b.subject_id
WHERE a.school_year = #{schoolScore.schoolYear}
GROUP BY subject_id, teacher_id
LIMIT #{schoolScore.start}, #{schoolScore.size}
